package JavaBasics;

public class IncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		int j = i++;
		
		System.out.println(i);
		System.out.println(j);
		
		int a = 1;
		int b = ++a;
		System.out.println(a);
		System.out.println(b);
	}

}
