package JavaBasics;

public class LoopsConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 1. While Loop
		int i = 1;
		
		while(i<=10) {
			System.out.println(i);
			i = i +1;
		}
		
		System.out.println("*************");
		
		for(int j=1;j<=10;j++) {
			System.out.println(j);
		}

	}

}
